"use client"
import { useState } from "react"
import { authAPI, tokenManager } from "../services/api"

const SignupForm = ({ onSignupSuccess }) => {
  const [formData, setFormData] = useState({ name: "", email: "", password: "", confirmPassword: "" })
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value })

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    if (formData.password !== formData.confirmPassword) { setError("Passwords do not match"); setLoading(false); return }
    if (formData.password.length < 6) { setError("Password must be at least 6 characters"); setLoading(false); return }

    try {
      const res = await authAPI.register(formData)
      if (!res.data?.data?.token) throw new Error("No token received")
      tokenManager.setToken(res.data.data.token)
      onSignupSuccess()
    } catch (err) {
      console.error("Signup error full:", err)
      setError(err.response?.data?.message || err.message || "Registration failed")
    } finally {
      setLoading(false)
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Full Name" required />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
      <input name="password" type="password" value={formData.password} onChange={handleChange} placeholder="Password" required />
      <input name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} placeholder="Confirm Password" required />
      {error && <div className="error">{error}</div>}
      <button type="submit" disabled={loading}>{loading ? "Creating Account..." : "Sign Up"}</button>
    </form>
  )
}

export default SignupForm
